import React, { useState } from 'react'
import { GET_MONEY, WASTE_MONEY } from './moneyReducer'
import { useDispatch } from 'react-redux'


export default function Form() {
    const [purchase, setPurchase] = useState('')
    const [cash, setCash] = useState(0)
    const dispatch = useDispatch()
    const handleSubmit = e => {
        e.preventDefault()
        if(cash > 0){
            dispatch({type: GET_MONEY, payload: {purchase: purchase, cash: cash}})
        }else{
            dispatch({type: WASTE_MONEY, payload: {purchase: purchase, cash: cash}})
        }
    }

  return (
    <form onSubmit={handleSubmit}>
        <label>Purchase</label>
        <input onChange={e => setPurchase(e.target.value)} type="text" />
        <input onChange={e => setCash(++e.target.value)} type="number" />
        <button onClick={handleSubmit} type="button">ADD</button>
    </form>
  )
}
