import React, { useLayoutEffect } from 'react'
import { useSelector } from 'react-redux'

export default function History() {

    let history = JSON.parse(localStorage.getItem('history'))
    let updatedHistory = useSelector(state => state.money.history)
    if(updatedHistory) history.unshift(updatedHistory)
    console.log(history)

    useLayoutEffect(() => {
        localStorage.removeItem('history')
        localStorage.setItem('history', JSON.stringify(updatedHistory))
    }, [history])
    
  return (
    <div className='history'>
        {history.map(el => 
            <div key={el.cash} className='history-block'>
                <h3>{el.purchase}</h3>
                {el.status === 'get' ? 
                    <div className="price green">{el.cash}$</div>
                    :
                    <div className="price red">{el.cash}$</div>
                }
            </div>
        )}
    </div>
  )
}
