import React, { useState } from 'react'
import { GET_MONEY, WASTE_MONEY } from './moneyReducer'
import { useDispatch } from 'react-redux'


export default function Form() {
    const [purchase, setPurchase] = useState('')
    let [cash, setCash] = useState()
    const dispatch = useDispatch()
    const handleSubmit = e => {
        e.preventDefault()
        if(!Number(cash) || purchase === '') return
        
        if(cash > 0){
            dispatch(GET_MONEY({purchase: purchase, cash: Number(cash), status: 'get'}))
        }else{
            dispatch(WASTE_MONEY({purchase: purchase, cash: Number(cash), status: 'waste'}))
        }

        setCash()
        setPurchase('')
    }

  return (
    <form onSubmit={handleSubmit}>
        <label>Purchase</label>
        <input onChange={e => setPurchase(e.target.value)} value={purchase} type="text" />
        <input onChange={e => setCash(e.target.value)} value={cash} placeholder='0' type="text" />
        <button onSubmit={handleSubmit}>ADD</button>
    </form>
  )
}
