import React, { useState } from 'react'
import { GET_MONEY, WASTE_MONEY } from './moneyReducer'
import { useDispatch } from 'react-redux'


export default function Form() {
    const [purchase, setPurchase] = useState('')
    let [cash, setCash] = useState()
    const dispatch = useDispatch()
    const handleSubmit = (e, status) => {
        e.preventDefault()
        if(status === 'get') dispatch(GET_MONEY({purchase: purchase, cash: Number(cash), status: 'get'}))
        else dispatch(WASTE_MONEY({purchase: purchase, cash: Number(cash), status: 'waste'}))
        setCash()
        setPurchase('')
    }

  return (
    <form onSubmit={handleSubmit}>
        <label>Purchase or Income</label>
        <input onChange={e => setPurchase(e.target.value)} value={purchase} type="text" />
        <input onChange={e => setCash(e.target.value)} value={cash} placeholder='0' type="number" />
        <div className="money-btns">
            <button className='btn-income' onClick={e => handleSubmit(e, 'get')}>INCOME</button>
            <button className='btn-purchase' onClick={e =>handleSubmit(e, ) }>PURCHASE</button>
        </div>
    </form>
  )
}
