import React, { useState } from 'react'
import { GET_MONEY, WASTE_MONEY } from './moneyReducer'
import { useDispatch } from 'react-redux'


export default function Form() {
    const [purchase, setPurchase] = useState('')
    let [cash, setCash] = useState(0)
    const dispatch = useDispatch()
    const handleSubmit = e => {
        if(!++(cash)) return
        console.log(cash)
        e.preventDefault()
        cash = (cash)
        if(cash > 0){
            dispatch(GET_MONEY({purchase: purchase, cash: cash}))
        }else{
            dispatch(WASTE_MONEY({purchase: purchase, cash: cash}))
        }
    }

  return (
    <form onSubmit={handleSubmit}>
        <label>Purchase</label>
        <input onChange={e => setPurchase(e.target.value)} type="text" />
        <input onChange={e => setCash(e.target.value)} type="text" />
        <button onClick={handleSubmit} type="button">ADD</button>
    </form>
  )
}
